import React from "react";
import {

    toggleIsFetching,
    setAuthData,
} from "../../redux/auth-reducer";
import {connect} from "react-redux";
import * as axios from "axios";
import Navbar from "./Navbar";

let mapStateToProps = (state) => {
    return {
        isFetching: state.auth.isFetching,
        data:state.auth.data,
    };
};
class NavbarAPIContainer extends React.Component {

    componentDidMount() {
        debugger;
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {withCredentials:true})
            .then(response => {
                this.props.setAuthData(response.data);
                this.props.toggleIsFetching(false);
            })
        debugger;
    }

    render() {
        return(<Navbar login = {this.data.login}/>)
    }
}
const UsersContainer = connect(mapStateToProps, {setAuthData,toggleIsFetching})(NavbarAPIContainer);
export default UsersContainer;
