import React from "react";
import s from "./Dialogs.module.css"
import UserItem from "./UsersItem/UsersItem";
import Message from "./Messages/Messages";



const Dialogs =(props)=>{
    debugger;

    let sendMessage =()=>{
        alert(messageCurrentValue.current.value);
    }
    let changedMessage =()=>{
        console.log("messagechanged")
    }

    let messageCurrentValue = React.createRef();
    let usersElements = props.dialogsPage.users.map(u => <UserItem key={m.id} id={u.id} name={u.name}/>); {/* обработка массива users с помощью map в jsx итемы*/}
    let messageElements = props.dialogsPage.messages.map(m=> <Message key={m.id} message={m.message} id={m.id}/>);
    return(
        <div className={s.dialogs}>
            <div className={s.users}>
                {usersElements}
            </div>
            <div className={s.messages}>
                {messageElements}
                <div className={s.area}>
                    <textarea ref={messageCurrentValue}  onChange={changedMessage}/>
                    <div>
                        <button onClick={sendMessage}>SEND</button>
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Dialogs;
