import React from "react";
import s from "./Dialogs.module.css"
import userIcon from "../../common/img/user.png"
import {NavLink} from "react-router-dom";


const UserItem = (props)=>{
    return(
        <div className={s.user}><img src={userIcon} alt=""/><NavLink to={`/dialogs/${props.id}`}> {props.name}</NavLink></div>
    )
}

const Message = (props) =>{
    return(
    <div  className={s.message}>{props.message}</div>
    )
}

let users = [
    {id:1,name:"Dimych"},
    {id:2,name:"Ivan"},
    {id:3,name:"Sveta"},
    {id:4,name:"Olga"},
    {id:5,name:"Sasha"},
   ]
let messages = [
    {id:1,message:"hello"},
    {id:2,message:"WHats up?"},
    {id:3,message:"How is your name?"},
    {id:4,message:"bla bla bla"},
    {id:5,message:"Yo"},
    {id:6,message:"Hello from Yata with love"},
    ]

let usersElements = users.map(u => <UserItem id={u.id} name={u.name}/>); {/* обработка массива users с помощью map в jsx итемы*/}
let messageEleent = messages.map(m=> <Message message={m.message} id={m.id}/>);


const Dialogs =()=>{
    return(
        <div className={s.dialogs}>
            <div className={s.users}>
                {usersElements}
            </div>
            <div className={s.messages}>
                {messageEleent}
            </div>
        </div>
    )
}
export default Dialogs;
