import s from "./Users.module.css"
import userIcon from "./../../common/img/userIcon.png"
import {NavLink} from "react-router-dom";


const User =(props)=>{
    return (
        <div>

            <div className={s.userItem} key={props.u.id}>
                <h3>name: {props.u.name}</h3>
                <li><NavLink   to={`/profile/${props.u.id}`}>
                    <img src={props.u.photos.small ? props.u.photos.small : userIcon} alt=""/>
                </NavLink></li>
                <br/>
                <span>status : {props.u.status}</span>
                <br/>
                <span>user id: {props.u.id}</span>
                <br/>
                {props.u.followed ?
                    <button disabled={props.followingInProgress.some(id=> id === props.u.id)} onClick={() => {
                        debugger;
                        props.unfollowUsersThunkCreator(props.u.id);

                    }}>unfollow</button> :
                    <button disabled={props.followingInProgress.some(id=> id === props.u.id)} onClick={() => {
                        debugger;
                        props.followUsersThunkCreator(props.u.id);

                    }}> follow</button>}}
            </div>))}


        </div>
    )}
export default User;
