import React, {useState} from "react";
import s from "../Profile.module.css"




const ProfileStatus= (props)=>{
    // хуки
    let[editMode,setEditMode] = useState(false);
    let[status, setStatus]=useState(props.status);
    const activatedEditMode=()=>{
        setEditMode(true);
    }
    const deactivatedMode =(e)=>{
        setEditMode(false);
        setStatus(e.currentValue.value);
        // props.changeStatus(e.currentValue.value);
    }
    return(
        <div>
            {!editMode && <span onDoubleClick={activatedEditMode}> <b>status: </b>{props.status}</span>}
            {editMode && <div><input autoFocus={true} onBlur={deactivatedMode}  value={status} type="text" /></div>}
        </div>
    )
            }

export default ProfileStatus;
