import React from "react";
import s from "./Users.module.css"
import userIcon from "./../../common/img/userIcon.png"


const Users =(props)=>{
    // debugger;
    // let follow =(userId)=>{
    //     debugger;
    //     props.follow(userId);
    // }
    // let unfollow =(userId)=>{
    //     debugger;
    //     props.unFollow(userId);
    // }
    // debugger;
    return(
        <div> {props.usersPage.users.map(u=>(<div className={s.userItem} key={u.id}>
            <h3>{u.fullName}</h3><img src={userIcon} alt=""/>
            <br/>
            <span>{u.status}</span>
            <br/>
            <span>{u.location.city}</span>
            <br/>
            <span>{u.location.country}</span><br/>
            <span>{u.id}</span>
            <br/>
            {u.follow ? <button onClick={()=>{props.unFollow(u.id)}}>unfollow</button> : <button onClick={()=>{props.follow(u.id)}}> follow</button>}

        </div>))}</div>
    )
}
export default Users;
