        messages: [
            {id: 1, message: "hello"},
            {id: 2, message: "WHats up?"},
            {id: 3, message: "How is your name?"},
            {id: 4, message: "bla bla bla"},
            {id: 5, message: "Yo"},
            {id: 6, message: "Hello from Yata with love"}
        ],
        currentValue:""
    },
}
let  dialogsReducer =(state = initialState,action)=>{
    switch (action.type) {
        case "ADD-MESSAGE":
            debugger;
            state.messages.push({id: 7, message: state.currentValue})
            state.currentValue = "";
            return state;
        case "MESSAGE-CHANGED":
            debugger;
            state.currentValue = action.value;
            return state;
        default:
            return state;


    }

}
export default dialogsReducer;
