import React from "react";
import s from "../Profile.module.css"


class ProfileStatus extends React.Component{

     state ={
        editMode:false
    }
    activatedMode =()=>{
         this.setState({editMode:true})
    }
    deactivatedMode =()=>{
        this.setState({editMode:false})
    }
    render() {
        if(!this.state.editMode)return <div><span onClick={this.activatedMode}> state</span></div>
        else return <div><input onBlur={this.deactivatedMode} type="text" value={"status"}/></div>
    }
}
export default ProfileStatus;
